mapper.select:
SELECT * FROM ( SELECT ROWNUM AS RNUM, A.* FROM ( SELECT A.METADATASEQN ,A.FILEIDENTIFIER ,( SELECT (SELECT CODE_NM FROM SOS_CODE_T C WHERE C.CODE = B.ROLE AND C.PARENT_CODE_SEQ_N = 10200) AS ROLE FROM WMO_CI_RESPONSIBLEPARTY B WHERE B.METADATASEQN = A.METADATASEQN
AND ROWNUM <= 1
) AS ROLE ,A.DATESTAMP ,( SELECT CODE_NM FROM SOS_CODE_T WHERE PARENT_CODE_SEQ_N = 10800 AND CODE = (SELECT C.TOPICCATEGORY FROM WMO_MD_DATAIDENTIFICATION C WHERE C.METADATASEQN = A.METADATASEQN) ) AS TOPICCATEGORY FROM WMO_MD_METADATA A WHERE 1 = 1
AND A.METADATASEQN IN ( SELECT B.METADATASEQN FROM WMO_MD_KEYWORDS A, WMO_MD_DATAIDENTIFICATION B WHERE A.KEYWORD LIKE '%' || #{search_text} || '%' AND B.DATAIDENTIFICATIONSEQN = A.DATAIDENTIFICATIONSEQN )
ORDER BY A.METADATASEQN DESC ) A
) WHERE RNUM >= #{startRow} AND RNUM <= #{endRow}

SELECT MAINTENANCEINFORMATIONSEQN ,METADATASEQN ,DATAIDENTIFICATIONSEQN ,MAINTENANCEANDUPDATEFREQUENCY ,USERDEFINEDMAINTENANCEFREQUENC ,UPDATESCOPE FROM WMO_MD_MAINTENANCEINFORMATION A WHERE
A.METADATASEQN= #{metadataseqn}
A.DATAIDENTIFICATIONSEQN = ${dataidentificationseqn}

SELECT RESPONSIBLEPARTYSEQN ,METADATASEQN ,MAINTENANCEINFORMATIONSEQN ,DATAIDENTIFICATIONSEQN ,CITATIONSEQN ,IDENTIFIERSEQN ,DISTRIBUTORSEQN ,INDIVIDUALNAME ,ORGANISATIONNAME ,POSITIONNAME ,ROLE FROM WMO_CI_RESPONSIBLEPARTY A WHERE
A.METADATASEQN= #{metadataseqn}
A.MAINTENANCEINFORMATIONSEQN= #{maintenanceinformationseqn}
A.DATAIDENTIFICATIONSEQN= #{dataidentificationseqn}
A.CITATIONSEQN= #{citationseqn}
A.IDENTIFIERSEQN= #{identifierseqn}
A.DISTRIBUTORSEQN= #{distributorseqn}

SELECT ONLINERESOURCESEQN ,CONTACTSEQN ,EXTENSIONINFORMATIONSEQN ,LINKAGE ,PROTOCOL ,NAME ,DESCRIPTION ,DIGITALTRANSFEROPTIONSSEQN FROM WMO_CI_ONLINERESOURCE A WHERE
A.CONTACTSEQN= #{contactseqn}
A.EXTENSIONINFORMATIONSEQN= #{extensioninformationseqn}
A.DIGITALTRANSFEROPTIONSSEQN= #{digitaltransferoptionsseqn}

SELECT CITATIONSEQN ,DATAIDENTIFICATIONSEQN ,KEYWORDSSEQN ,CONFORMANCERESULTSEQN ,TITLE ,ALTERNATIVETITLE ,DATES ,IDENTIFIER ,CITEDRESPONSIBLEPARTY ,PRESENTATIONFORM FROM WMO_CI_CITATION A WHERE
A.DATAIDENTIFICATIONSEQN= #{dataidentificationseqn}
A.KEYWORDSSEQN= #{keywordsseqn}
A.CONFORMANCERESULTSEQN= #{conformanceresultseqn}

SELECT IDENTIFIERSEQN ,CITATIONSEQN ,GEOGRAPHICDESCRIPTIONSEQN ,AUTHORITY ,CODE ,DOMAINCONSISTENCYSEQN FROM WMO_MD_IDENTIFIER A WHERE
A.CITATIONSEQN= #{citationseqn}
A.GEOGRAPHICDESCRIPTIONSEQN= #{geographicdescriptionseqn}
A.DOMAINCONSISTENCYSEQN= #{domainconsistencyseqn}

SELECT 1 AS G_TYPE ,GEOGRAPHICBOUNDINGBOXSEQN ,EXTENTSEQN ,WESTBOUNDLONGITUDE ,EASTBOUNDLONGITUDE ,SOUTHBOUNDLATITUDE ,NORTHBOUNDLATITUDE ,NULL AS GEOGRAPHICDESCRIPTIONSEQN ,NULL AS EXTENTSEQN ,NULL AS GEOGRAPHICIDENTIFIER FROM WMO_EX_GEOGRAPHICBOUNDINGBOX A WHERE A.EXTENTSEQN = #{extentseqn}
UNION
SELECT 2 AS G_TYPE ,NULL AS GEOGRAPHICBOUNDINGBOXSEQN ,NULL AS EXTENTSEQN ,NULL AS WESTBOUNDLONGITUDE ,NULL AS EASTBOUNDLONGITUDE ,NULL AS SOUTHBOUNDLATITUDE ,NULL AS NORTHBOUNDLATITUDE ,GEOGRAPHICDESCRIPTIONSEQN ,EXTENTSEQN ,GEOGRAPHICIDENTIFIER FROM WMO_EX_GEOGRAPHICDESCRIPTION B WHERE B.EXTENTSEQN = #{extentseqn}

SELECT CONSTRAINTSSEQN ,METADATASEQN ,DATAIDENTIFICATIONSEQN ,USELIMITATION FROM WMO_MD_CONSTRAINTS A WHERE
A.METADATASEQN= #{metadataseqn}
A.DATAIDENTIFICATIONSEQN= #{dataidentificationseqn}

SELECT FORMATSEQN ,DISTRIBUTIONSEQN ,DISTRIBUTORSEQN ,NAME ,VERSION ,SPECIFICATION ,FORMATDISTRIBUTOR FROM WMO_MD_FORMAT A WHERE
A.DISTRIBUTIONSEQN= #{distributionseqn}
A.DISTRIBUTORSEQN= #{distributorseqn}

SELECT DISTRIBUTORSEQN ,FORMATSEQN ,DISTRIBUTIONSEQN ,DISTRIBUTORCONTACT ,DISTRIBUTORFORMAT ,DISTRIBUTORTRANSFEROPTIONS FROM WMO_MD_DISTRIBUTOR A WHERE
A.DISTRIBUTIONSEQN= #{distributionseqn}
A.FORMATSEQN= #{formatseqn}

SELECT DIGITALTRANSFEROPTIONSSEQN ,DISTRIBUTIONSEQN ,DISTRIBUTORSEQN ,ONLINES FROM WMO_MD_DIGITALTRANSFEROPTIONS A WHERE
A.DISTRIBUTIONSEQN= #{distributionseqn}
A.DISTRIBUTORSEQN= #{distributorseqn}

