mapper.delete:

DELETE FROM WMO_MD_MAINTENANCEINFORMATION WHERE 

	METADATASEQN = #{metadataseqn}


	DATAIDENTIFICATIONSEQN = #{dataidentificationseqn}


DELETE FROM 
	WMO_CI_RESPONSIBLEPARTY 
WHERE 

	METADATASEQN = #{metadataseqn}


	MAINTENANCEINFORMATIONSEQN = #{maintenanceinformationseqn}


	DATAIDENTIFICATIONSEQN = #{dataidentificationseqn}


	CITATIONSEQN = #{citationseqn}


	IDENTIFIERSEQN = #{identifierseqn}


	DISTRIBUTORSEQN = #{distributorseqn}


DELETE FROM WMO_MD_CONSTRAINTS 
WHERE 

	METADATASEQN = #{metadataseqn}


	DATAIDENTIFICATIONSEQN = #{dataidentificationseqn}


DELETE FROM WMO_MD_IDENTIFIER 
WHERE

	CITATIONSEQN = #{citationseqn}


	GEOGRAPHICDESCRIPTIONSEQN = #{geographicdescriptionseqn}


	DOMAINCONSISTENCYSEQN = #{domainconsistencyseqn}


DELETE FROM WMO_CI_CITATION WHERE 

	DATAIDENTIFICATIONSEQN = #{dataidentificationseqn}


	KEYWORDSSEQN = #{keywordsseqn}


	CONFORMANCERESULTSEQN = #{conformanceresultseqn}


DELETE FROM WMO_MD_FORMAT WHERE

	DISTRIBUTIONSEQN = #{distributionseqn}


	DISTRIBUTORSEQN = #{distributorseqn}


DELETE FROM WMO_MD_DISTRIBUTOR WHERE 

	DISTRIBUTIONSEQN = #{distributionseqn}


	FORMATSEQN = #{formatseqn}


DELETE FROM WMO_MD_DIGITALTRANSFEROPTIONS WHERE 

	DISTRIBUTIONSEQN = #{distributionseqn}


	DISTRIBUTORSEQN = #{distributorseqn}


	{call
        declare
        begin   

		DELETE FROM WMO_EX_GEOGRAPHICBOUNDINGBOX WHERE EXTENTSEQN = #{extentseqn};

		DELETE FROM WMO_EX_GEOGRAPHICDESCRIPTION WHERE EXTENTSEQN = #{extentseqn};

		end
    }

mapper.update:

	UPDATE WMO_MD_METADATA SET 
        FILEIDENTIFIER = #{fileidentifier}        
       ,LANGUAGE = #{language}
       ,CHARACTERSET = #{characterset}
       ,HIERARCHYLEVEL = #{hierarchylevel}       
       ,HIERARCHYLEVELNAME = #{hierarchylevelname}  
       ,DATESTAMP = #{datestamp}                
       ,METADATASTANDARDNAME = #{metadatastandardname} 
       ,METADATASTANDARDVERSION = #{metadatastandardversion}
       ,LOCALE = #{locale}               
     WHERE 
     	METADATASEQN = #{metadataseqn}
